field.onclick=function(l){let t=this.getBoundingClientRect(),e={top:l.clientY-t.top-field.clientTop-ball.clientHeight/2,left:l.clientX-t.left-field.clientLeft-ball.clientWidth/2};e.top<0&&(e.top=0),e.left<0&&(e.left=0),e.left+ball.clientWidth>field.clientWidth&&(e.left=field.clientWidth-ball.clientWidth),e.top+ball.clientHeight>field.clientHeight&&(e.top=field.clientHeight-ball.clientHeight),ball.style.left=e.left+"px",ball.style.top=e.top+"px"};